package com.aiokleo.rgb_string_to_color;

import java.awt.*;
import java.awt.image.BufferedImage;
import java.io.File;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.stream.Collectors;
import javax.imageio.ImageIO;

public class HexStringToImage {
    public static void main(String[] args) {
        // Hexadecimal string
        String hexString = "";
        String[] lines = hexString.trim().split("(?<=\\G.{241})");
        ArrayList<String> list = new ArrayList<>();
        for (String line : lines) {
            list.add(line.substring(1));
        }
        String imageLines = String.join("", list);

        //Next Step


        // Calculate the width and height of the image
        int width = 270; // You can adjust this based on your needs
        int height = imageLines.length() / (width * 2);

        // Create a BufferedImage
        BufferedImage image = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);

        // Set pixel values from the hexadecimal string
        int index = 0;
        for (int y = 0; y < height; y++) {
            for (int x = 0; x < width; x++) {
                int colorValue = Integer.parseInt(imageLines.substring(index, index + 2), 16);
                int rgb = (colorValue << 16) | (colorValue << 8) | colorValue;
                image.setRGB(x, y, rgb);
                index += 2;
//                // Extract the next two characters from the hexadecimal string
//                String hexValue = imageLines.substring(index, index + 2);
//                index += 2;
//
//                // Parse the hexadecimal value to an integer
//                int intValue = Integer.parseInt(hexValue, 16);
//
//                // Set the pixel color based on the parsed value
//                Color color = new Color(intValue);
//                image.setRGB(x, y, color.getRGB());
            }
        }

        // Save the image to a file
        saveImage(image, "output.png");
    }

    // For saving the image to a file
    public static void saveImage(BufferedImage image, String filename) {
        try {
            File output = new File("/Users/cmed/Desktop/5k/" + filename);
            ImageIO.write(image, "png", output);
            System.out.println("Image saved to " + filename);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}
